document.addEventListener("DOMContentLoaded",()=>{const e=new Audio("Assets/music/80s-Delorean-Weeknd-DuaLipa-Pop-Synthwave.mp3"),t=document.getElementById("toggle-music-button"),n=t.querySelector(".icon-play"),o=t.querySelector(".icon-pause");function c(){e.paused?e.play().then(()=>{n.style.display="inline",o.style.display="none"}).catch(e=>{console.error("Erreur lors de la lecture de la musique:",e)}):(e.pause(),n.style.display="none",o.style.display="inline")}t.addEventListener("click",c)});